SELECT W.ID,WP.AGE,W.COINS_NEEDED,W.POWER FROM WANDS AS W JOIN WANDS_PROPERTY AS WP ON W.CODE = WP.CODE WHERE WP.IS_EVIL = 0 AND W.COINS_NEEDED = (SELECT MIN(COINS_NEEDED) FROM WANDS AS W1 JOIN WANDS_PROPERTY AS P1 ON W1.CODE = P1.CODE WHERE W1.POWER = W.POWER AND P1.AGE =WP.AGE) ORDER BY W.POWER DESC,WP.AGE DESC
